import { Component, OnInit, Output, EventEmitter } from '@angular/core';
import { UserProfileService, Profile, Story, StoryResponse } from '../services/user-profile.service';

@Component({
  selector: 'app-story-generation',
  templateUrl: './story-generation.component.html',
  styleUrls: ['./story-generation.component.css']
})
export class StoryGenerationComponent implements OnInit {
  @Output() backToProfile = new EventEmitter<void>();

  profile: Profile | null = null;
  currentStory: Story | null = null;
  isLoading: boolean = false;
  error: string | null = null;
  showStory: boolean = false;
  currentPanelIndex: number = 0;

  constructor(private userProfileService: UserProfileService) {}

  ngOnInit(): void {
    // Get profile from service (set by profile analysis component)
    this.profile = (this.userProfileService as any).currentProfile;

    if (this.profile) {
      // Auto-generate story on load
      this.generateStory();
    } else {
      this.error = 'No profile available. Please analyze profile first.';
    }
  }

  generateStory(): void {
    if (!this.profile) {
      this.error = 'No profile available';
      return;
    }

    this.isLoading = true;
    this.error = null;
    this.showStory = false;
    this.currentStory = null;
    this.currentPanelIndex = 0;

    this.userProfileService.generateStory(this.profile).subscribe({
      next: (response: StoryResponse) => {
        this.currentStory = response.story;
        this.isLoading = false;
        this.showStory = true;
      },
      error: (error) => {
        this.error = error.error?.detail || 'Failed to generate story';
        this.isLoading = false;
        console.error('Story generation error:', error);
      }
    });
  }

  nextPanel(): void {
    if (this.currentStory && this.currentPanelIndex < this.currentStory.panels.length - 1) {
      this.currentPanelIndex++;
    }
  }

  previousPanel(): void {
    if (this.currentPanelIndex > 0) {
      this.currentPanelIndex--;
    }
  }

  goToPanel(index: number): void {
    if (this.currentStory && index >= 0 && index < this.currentStory.panels.length) {
      this.currentPanelIndex = index;
    }
  }

  onStartQuiz(): void {
    // Store story in service and navigate to quiz
    if (this.currentStory) {
      (this.userProfileService as any).currentStory = this.currentStory;
      if (this.userProfileService) {
        (this.userProfileService as any).navigateToQuiz?.();
      }
    }
  }

  goBackToProfile(): void {
    this.backToProfile.emit();
  }
}

<div class="story-generation-container">
  <div class="header">
    <h1>📚 Story Generation</h1>
    <button class="btn-back" (click)="goBackToProfile()">← Back to Profile</button>
  </div>

  <!-- Loading State -->
  <div class="loading-section" *ngIf="isLoading">
    <div class="loading">
      <div class="spinner"></div>
      <h2>Creating Your Story...</h2>
      <p>We're crafting a personalized story just for you!</p>
    </div>
  </div>

  <!-- Error State -->
  <div class="error-section" *ngIf="error && !isLoading">
    <div class="error-card">
      <div class="error-icon">❌</div>
      <h2>Story Generation Failed</h2>
      <p>{{ error }}</p>
      <button (click)="generateStory()" class="btn-retry">Retry</button>
    </div>
  </div>

  <!-- Story Display -->
  <div class="story-section" *ngIf="showStory && currentStory">
    <!-- Story Header -->
    <div class="story-header">
      <h2>{{ currentStory.title }}</h2>
      <div class="story-meta">
        <span class="badge concept-badge">{{ currentStory.concept }}</span>
        <span class="badge difficulty-badge">{{ currentStory.difficulty }}</span>
        <span class="badge theme-badge">{{ currentStory.theme }}</span>
      </div>
    </div>

    <!-- Story Panels Navigation -->
    <div class="panels-navigation" *ngIf="currentStory.panels.length > 0">
      <div class="panel-indicators">
        <button 
          *ngFor="let panel of currentStory.panels; let i = index"
          class="panel-indicator"
          [class.active]="i === currentPanelIndex"
          (click)="goToPanel(i)"
        >
          {{ i + 1 }}
        </button>
      </div>
      <div class="panel-controls">
        <button 
          class="btn-nav" 
          (click)="previousPanel()"
          [disabled]="currentPanelIndex === 0"
        >
          ← Previous
        </button>
        <span class="panel-counter">
          Panel {{ currentPanelIndex + 1 }} of {{ currentStory.panels.length }}
        </span>
        <button 
          class="btn-nav" 
          (click)="nextPanel()"
          [disabled]="currentPanelIndex === currentStory.panels.length - 1"
        >
          Next →
        </button>
      </div>
    </div>

    <!-- Current Panel Display -->
    <div class="panel-display" *ngIf="currentStory.panels[currentPanelIndex]">
      <div class="panel-card">
        <div class="panel-number-large">{{ currentPanelIndex + 1 }}</div>
        <div class="panel-content">
          <h3>Panel {{ currentPanelIndex + 1 }}</h3>
          <p class="panel-text">{{ currentStory.panels[currentPanelIndex].text }}</p>
        </div>
      </div>
    </div>

    <!-- All Panels Grid View -->
    <div class="all-panels-section">
      <h3>📖 Complete Story</h3>
      <div class="panels-grid">
        <div 
          class="panel-mini-card" 
          *ngFor="let panel of currentStory.panels; let i = index"
          [class.active]="i === currentPanelIndex"
          (click)="goToPanel(i)"
        >
          <div class="panel-mini-number">{{ panel.panelId }}</div>
          <p class="panel-mini-text">{{ panel.text }}</p>
        </div>
      </div>
    </div>

    <!-- Full Story Text -->
    <div class="full-story-section">
      <h3>📝 Full Story Text</h3>
      <div class="full-story-text">
        <p>{{ currentStory.fullStoryText }}</p>
      </div>
    </div>

    <!-- Learning Points -->
    <div class="learning-points-section">
      <h3>🎯 Key Learning Points</h3>
      <div class="learning-points-list">
        <div 
          class="learning-point-item" 
          *ngFor="let point of currentStory.learningPoints; let i = index"
        >
          <div class="point-number">{{ i + 1 }}</div>
          <div class="point-text">{{ point }}</div>
        </div>
      </div>
    </div>

    <!-- Next Concept Recommendation -->
    <div class="next-concept-section" *ngIf="currentStory.nextRecommendedConcept">
      <div class="next-concept-card">
        <h4>💡 Next Recommended Concept</h4>
        <p>{{ currentStory.nextRecommendedConcept }}</p>
      </div>
    </div>

    <!-- Action Buttons -->
    <div class="action-section">
      <button 
        class="btn-primary btn-large" 
        (click)="onStartQuiz()"
      >
        ✅ Start Quiz
      </button>
      <button 
        class="btn-secondary" 
        (click)="generateStory()"
      >
        🔄 Generate New Story
      </button>
    </div>
  </div>
</div>
